import React, { useState } from 'react'

const App = () => {
    const [resource,setResource] = useState('posts')
  return (
    <div>
      <div>
        <button onClick={() => setResource('posts')}>
          Posts
        </button>
        <button onClick={() => this.setState({resource: 'todos'})}>
          Todos
        </button>
      </div>
      {this.state.resource}
    </div>
  );
}

export default App
