import React from 'react';
import ReactDOM from 'react-dom';

function getButtonData() {
  return 'clicke here 1';
}
const App = () => {
  //const buttonText = 'Click here';
  return ( <div >
    <label> Name: </label> <input id = "name"
    type = "text" / >
    <button style = {
      {
        backgroundColor: 'red',
        color: 'white'
      }
    } > {
      getButtonData()
    } </button> </div>
  );
};

ReactDOM.render( <App /> ,
  document.querySelector('#root')
);



